import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Login.css';

export default function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const [showPass, setShowPass] = useState(false);
  const nav = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!email || !pass) {
      alert('Oops! Please fill in both email and password.');
      return;
    }

    const user = { email };
    onLogin(user);
    nav('/');
  };

  return (
    <div className="login-page">
      <div className="login-card">
        <h2 className="title">Hello, Friend!</h2>
        <p className="subtitle">Enter your details to continue.</p>

        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <input
              type="email"
              placeholder="e.g., youremail@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <input
              type={showPass ? 'text' : 'password'}
              placeholder="Enter your password"
              value={pass}
              onChange={(e) => setPass(e.target.value)}
              required
            />
            <span
              className="toggle-pass"
              onClick={() => setShowPass(!showPass)}
            >
              {showPass ? 'Hide' : 'Show'}
            </span>
          </div>

          <button type="submit" className="login-btn">Login</button>
        </form>

        <p className="signup-text">
          New here? <Link to="/signup">Create an account</Link> and start your journey!
        </p>
      </div>
    </div>
  );
}
