import React, { useEffect, useState } from 'react';
import GoalCard from '../components/GoalCard';

export default function Goals() {
  // Retrieve goals from localStorage or initialize as empty
  const [goals, setGoals] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('cc_goals')) || [];
    } catch {
      return [];
    }
  });

  // Form inputs
  const [name, setName] = useState('');
  const [target, setTarget] = useState('');

  // Persist goals to localStorage whenever updated
  useEffect(() => {
    localStorage.setItem('cc_goals', JSON.stringify(goals));
  }, [goals]);

  // Add a new goal
  const handleAddGoal = (e) => {
    e.preventDefault();

    if (!name || !target) return;

    const newGoal = {
      id: Date.now(),
      name,
      target: parseFloat(target),
      saved: 0,
    };

    setGoals([newGoal, ...goals]);
    setName('');
    setTarget('');
  };

  return (
    <div className="page">
      <h2>Goals</h2>

      {/* Goal Creation Form */}
      <form className="card form-inline" onSubmit={handleAddGoal}>
        <input
          placeholder="Goal name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Target amount"
          value={target}
          onChange={(e) => setTarget(e.target.value)}
        />
        <button type="submit">Create Goal</button>
      </form>

      {/* Goals List */}
      <div className="grid-2">
        {goals.length === 0 && (
          <p className="muted">No goals yet. Create one to start saving.</p>
        )}

        {goals.map((g) => (
          <GoalCard key={g.id} goal={g} />
        ))}
      </div>
    </div>
  );
}
