import React, { useEffect, useState } from 'react';
import GoalCard from '../components/GoalCard';

export default function Goals() {
  const [goals, setGoals] = useState(() => {
    try { return JSON.parse(localStorage.getItem('cc_goals')) || []; } catch { return []; }
  });
  const [name, setName] = useState('');
  const [target, setTarget] = useState('');

  useEffect(()=>{ localStorage.setItem('cc_goals', JSON.stringify(goals)); }, [goals]);

  const add = (e)=>{
    e.preventDefault();
    if(!name||!target) return;
    setGoals([{id:Date.now(), name, target:parseFloat(target), saved:0}, ...goals]);
    setName(''); setTarget('');
  };

  return (
    <div className="page">
      <h2>Goals</h2>
      <form className="card form-inline" onSubmit={add}>
        <input placeholder="Goal name" value={name} onChange={(e)=>setName(e.target.value)} />
        <input placeholder="Target amount" value={target} onChange={(e)=>setTarget(e.target.value)} type="number" />
        <button type="submit">Create Goal</button>
      </form>

      <div className="grid-2">
        {goals.length===0 && <p className="muted">No goals yet. Create one to start saving.</p>}
        {goals.map(g => <GoalCard key={g.id} goal={g} />)}
      </div>
    </div>
  );
}
