import React, { useEffect, useState } from 'react';

export default function Budget() {
  const [budget, setBudget] = useState(() => parseFloat(localStorage.getItem('cc_budget')) || 0);
  const [input, setInput] = useState('');

  useEffect(()=>{ localStorage.setItem('cc_budget', budget); }, [budget]);

  const save = (e) => {
    e.preventDefault();
    const val = parseFloat(input);
    if (!isNaN(val)) setBudget(val);
    setInput('');
  };

  const spent = JSON.parse(localStorage.getItem('cc_expenses')||'[]').reduce((s,i)=>s+i.amount,0);
  const remaining = budget - spent;
  const pct = budget>0 ? Math.max(0, Math.min(100, Math.round((remaining / budget) * 100))) : 0;

  return (
    <div className="page">
      <h2>Budget Planner</h2>
      <div className="grid-2">
        <div className="card">
          <h4>Set Monthly Budget</h4>
          <form onSubmit={save} className="form-inline">
            <input placeholder="Monthly budget" value={input} onChange={(e)=>setInput(e.target.value)} type="number" />
            <button type="submit">Save</button>
          </form>
        </div>
        <div className="card">
          <h4>Overview</h4>
          <p>Budget: ${budget.toFixed(2)}</p>
          <p>Spent: ${spent.toFixed(2)}</p>
          <p>Remaining: ${remaining.toFixed(2)}</p>
          <div className="progress small"><div style={{width: pct + '%'}} /></div>
          {remaining <= (budget * 0.15) && budget>0 && <p className="warning">You're close to your budget limit!</p>}
        </div>
      </div>
    </div>
  );
}
