import React, { useState } from 'react';
import { categories } from '../utils/dummyData';

export default function ExpenseForm({ onAdd }) {
  const [name, setName] = useState('');
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState(categories[0]);

  const submit = (e) => {
    e.preventDefault();
    if (!name || !amount) return;
    onAdd({ id: Date.now(), name, amount: parseFloat(amount), category, date: new Date().toISOString().slice(0,10) });
    setName(''); setAmount(''); setCategory(categories[0]);
  };

  return (
    <form className="card form-inline" onSubmit={submit}>
      <input placeholder="Expense name" value={name} onChange={(e)=>setName(e.target.value)} />
      <input placeholder="Amount" type="number" value={amount} onChange={(e)=>setAmount(e.target.value)} />
      <select value={category} onChange={(e)=>setCategory(e.target.value)}>
        {categories.map(c=> <option key={c} value={c}>{c}</option>)}
      </select>
      <button type="submit">Add</button>
    </form>
  );
}
